INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (238, NULL, N'Aster', NULL, N'laevis', NULL, N'(Symphyotrichum laeve)', N'ASTLAEVI', N'The pale lavender flowers set against black stems and dark leaves give this aster an elegant charm. It is tall and open creating a substantial but delicate show in October. Full sun. 200cm. disease free', N'1L', 1, N'0000000000000', N'A\Aster laevis.JPG', NULL, NULL, NULL, 0, N'Default', 0, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (239, NULL, N'Aster', NULL, N'lateriflorus', N'Chloe''', N'(Symphyotrichum lateriflorum ''Chloe'') Calico Aster. ', N'ASTCHLO', N'Arching swags of pure white daisies with yellow eyes are borne on stiffly upright stems with gently arching laterals. But it just gets better and better until the centres transform into miniature Robin''s Pincushions. Petals reflex with age and take on a hint of silver pink. 1m', N'1L', 1, N'0000000000000', N'A\Aster-lateriflorus-Chloe-(1).jpg', NULL, NULL, NULL, 0, N'Default', 0, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (240, NULL, N'Aster', NULL, N'lateriflorus', N'''Horizontalis''', NULL, NULL, N'The branches of this 60cm shrubby aster arch out to give a tiered effect. Each in autumn is absolutely smothered in small white flowers with a large violet boss of stamens.', NULL, 1, N'0000000000000', N'A\Aster lateriflorus Horizontalis.jpg', NULL, NULL, NULL, 1, N'Default', 0, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (241, NULL, N'Aster', NULL, N'lateriflorus', N'''Prince''', N'(Symphyotrichum lateriflorum ''Prince'')', N'ASTPRIN', N'The branches of this 60cm shrubby aster arch out to give a lovely tiered effect. Each branch in autumn is absolutely smothered in small white flowers with a large violet boss of stamens. Folaige is dark and smoky all season.', N'1L', 1, N'0000000000000', N'A\Aster lateriflorus Prince #1.JPG', NULL, NULL, NULL, 0, N'Default', 0, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (242, NULL, N'Aster', NULL, N'novae-angliae', N'''Harrington''s Pink''', N'( Symphyotrichum novae-angliae ''Harrington''s Pink'' )', N'ASTHARPIN', N'A glory in September to October when it is a huge  mound of pale sugary pink flowers covered in bees  and butterflies. A large showy michelmas daisy  which is largely mildew resistant. 200cm', N'1L', 1, N'0000000000000', N'A\Aster novae-angliae Harringtons Pink.jpg', NULL, NULL, NULL, 0, N'Default', 0, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (243, NULL, N'Aster', NULL, N'novae-angliae', N'Helen Picton''', N'( Symphyotrichum novae-angliae Helen Picton'' )', N'ASTHELPIC', N'A shaggy collar of narrow purple tinted bracts from which uncurl a myriad of narrow petals in a deep rich royal purple. The flowers begin with a reasonable regularity and become more unruly as the flower ages. 90-100cm. Reckoned to be the best purple ', N'1L', 1, N'0000000000000', N'A\Aster-novae-angliae-Helen-Picton-(2).jpg', NULL, NULL, NULL, 0, N'Default', 0, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (244, NULL, N'Aster', NULL, N'novae-angliae', N'''James Ritchie''', N'( Symphyotrichum novae-angliae ''James Ritchie'' )', N'ASTNAJR', N'"Selected by Julie Ritchie of Hoo House Nursery and named for her son. The colour is a good rich cerise not so strident as ''Alma Potschke'' being a little darket in tone. The height is a good 4ft.INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (245, NULL, N'Aster', NULL, N'novae-angliae', N'Lye End Beauty''', N'( Symphyotrichum novae-angliae Lye End Beauty'' )', N'ASTLYEEB', N'"Falls somewhere in the middle in the colour stakes. The narrow petals are a rich but not strident cerise on their inner surface and a silvery pink on the reverse. With a dark ruff of narrow bracts and rose tinted stems it was certainly well named. 130-175cmINSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (246, NULL, N'Aster', NULL, N'novae-angliae', N'''Purple Dome''', N'( Symphyotrichum novae-angliae ''Purple Dome'' )', N'ASTPDO', N'Michaelmas Daisy - compact plants to 50cm 2'' become mounds of fringed daisy flowers in late summer and autumn. This variety has violet purple flowers. A wonderful plant for the autumn garden - no staking required!', N'1L', 1, N'0000000000000', N'A\Aster Purple Dome #1.JPG', N'A\Aster Purple Dome #2.JPG', N'A\Aster Purple Dome #3.JPG', NULL, 0, N'Default', 0, NULL, 0)
