INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (248, NULL, N'Aster', NULL, N'novae-angliae', N'''Wow''', N'( Symphyotrichum novae-angliae ''Wow'' )', N'ASTNAWOW', N'Possibly the very last of all the Asters to be in flower. Typical shaggy novae-angliae daisies. The colour is a rich magenta with a dark central boss - it makes me think of Vernonia ''MammuthOne of the best good and late and not one of those awful blue-pinks. to just under 2m', NULL, 1, N'0000000000000', N'A\Aster-novae-angliae-Wow-(1).jpg', NULL, NULL, NULL, 0, N'Default', 0, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (249, NULL, N'Aster', NULL, N'novi-angliae', N'Alma Potschke', N'( Symphyotrichum novi-angliae Alma Potschke )', N'ASTAAAP', N'A striking autumn flowering Aster with branching heads producing a mass of very bright cerise scarlet daisies warm enough to cheer the dullest day. Very easy to grow. Height 1.3m (4ft)', N'1L', 1, N'0000000000000', N'A\Aster novae-angliae Andenken an Alma Potschke #1.jpg', NULL, NULL, NULL, 0, N'Default', 0, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (250, NULL, N'Aster', NULL, N'novi-belgii', N'''Anita Webb''', N'( Symphyotrichum novi-belgii ''Anita Webb'' )', N'ASTNBANITA', N'One of the first to bloom. These Aster blooms open as perfectly domed sugar pink powder puffs. As they mature the petals reflex to reveal a golden eye surrounded by a frilly tutu of sugary pink with silver highlights - A real girly delight ! Flowers 3.5cm wide. Height 90cm. ', NULL, 1, N'0000000000000', N'A\Aster-novi-belgii-Anita-Webb.jpg', NULL, NULL, NULL, 1, N'Default', 0, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (251, NULL, N'Aster', NULL, N'novi-belgii', N'''Audrey''', N'( Symphyotrichum novi-belgii ''Audrey'' )', N'ASTNBAUD', N'"30cm. The short twiggy bush is literally smothered with beautiful semi-double lilac-blue daisies 3cm wide with yellow button eyes. Although technically semi-double the flowers open flat and appear as well formed singles. Mid September-October
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (252, NULL, N'Aster', NULL, N'novi-belgii', N'''Beechwood Rival''', N'( Symphyotrichum novi-belgii ''Beechwood Rival'' )', N'ASTBEECH', N'MICHAELMAS DAISY. A shorter growing Aster with semi-double dark purple-red flowers on 70cm high plants. Will thrive in sun or part shade. September-October', N'1L', 1, N'0000000000000', N'A\Aster novi-belgii Beechwood Rival.jpg', NULL, NULL, NULL, 1, N'Default', 0, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (253, NULL, N'Aster', NULL, N'novi-belgii', N'''Blue Lagoon''', N'( Symphyotrichum novi-belgii ''Blue Lagoon'' )', N'ASTBLUEL', N'The benefits of this hybrid Michaelmas daisy are a short compact habit combined with an abundance of clear blue flowers that  completely cover the plant. Blends beautifully with the rich tones of Autumn 40cm', N'1L', 1, N'0000000000000', N'A\Aster Blue Lagoon.JPG', NULL, NULL, NULL, 0, N'Default', 0, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (254, NULL, N'Aster', NULL, N'novi-belgii', N'''Blue Moon''', N'( Symphyotrichum novi-belgii ''Blue Moon'' )', N'ASTNBBMOO', N'"An intermediate height neat Aster with a rich profusion of pale lavender-blue flowers with appealing pale creamy yellow eyes. Only 50cm high and flowering from late September into October.INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (255, NULL, N'Aster', NULL, N'novi-belgii', N'''Brigitte''', N'( Symphyotrichum novi-belgii ''Brigitte'' )', N'ASTBRIG', N'MICHAELMAS DAISY. A shorter growing Aster with semi-double pale lavender flowers on 50cm high plants. Will thrive in sun or part shade. September-October', N'1L', 1, N'0000000000000', N'A\Aster Brigitte #3.JPG', NULL, NULL, NULL, 0, N'Default', 0, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (256, NULL, N'Aster', NULL, N'novi-belgii', N'''Chatterbox''', N'( Symphyotrichum novi-belgii ''Chatterbox'' )', N'ASTNBCHAT', N'"50cm. Rosy pink buds gradually unfurl lots of sugar pink petals to give a generous display of double pink daisies. At 50cm or below this is a cute little Aster. mid Sept into Oct. And it needs no staking ! Bred in the 1960''s by Bakers Nrs and named after the breeders granddaughter !INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (257, NULL, N'Aster', NULL, N'novi-belgii', N'''Coombe Fishacre''', N'( Symphyotrichum novi-belgii ''Coombe Fishacre'' )', N'ASTNBCOOMBF', N'"This Aster opens a myriad of small (2cm) single lilac-pink daisies on a short twiggy bush. Although classified as a novi-belgii hybrid it leans quite heavily in both flower shape and branching habit towards the style of a lateriflorus. 80cm October. Some mildew resistance.INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (258, NULL, N'Aster', NULL, N'novi-belgii', N'''Crimson Brocade''', N'( Symphyotrichum novi-belgii ''Crimson Brocade'' )', N'ASTCRIMB', N'The michaelmas daisies are such a welcome flourish at the end of the year with their glowing profusion of rich tones. This variety is a strong deep crimson semi-double with a bright golden button. 90cm', N'1L', 1, N'0000000000000', N'A\Aster novi-belgii Crimson Brocade.jpg', NULL, NULL, NULL, 0, N'Default', 0, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (259, NULL, N'Aster', NULL, N'novi-belgii', N'''Dazzler''', N'( Symphyotrichum novi-belgii ''Dazzler'' )', N'ASTNBDAZ', N'"At the National Collection of Aster this small flowered (2cm wide) mid pink semi-double Aster with dark eyes had me dribbling. A lovely colour and such a mass of blooms. A beauty that had to go home with me ! 90cm September to October.INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (260, NULL, N'Aster', NULL, N'novi-belgii', N'''Eventide''', N'( Symphyotrichum novi-belgii ''Eventide'' )', N'ASTEVEN', N'A richly coloured violet blue semi-double aster with a broad robust feel. With its bright yellow button centre; it''s makes a lovely complimentary accent amongst wilder pink michaelmas daisies. 90cm', N'1L', 1, N'0000000000000', N'A\Aster Eventide #1.JPG', N'A\Aster novi-belgii Eventide.jpg', NULL, NULL, 0, N'Default', 0, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (261, NULL, N'Aster', NULL, N'novi-belgii', N'Fellowship''', N'( Symphyotrichum novi-belgii Fellowship'' )', N'ASTFELL', N'Many petalled flowers are a dreamy candy-pink double with a fresh limey -green eye on newly opened flowers. As they age they seem to become fuller still as the petals curl and twist to form tutu of pink loveliness. excellent reputation for health and vigour. August-October. 75cm', N'1L', 1, N'0000000000000', N'A\Aster-Fellowship.jpg', NULL, NULL, NULL, 0, N'Default', 0, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (262, NULL, N'Aster', NULL, N'novi-belgii', N'''Gurney Slade''', N'( Symphyotrichum novi-belgii ''Gurney Slade'' )', N'ASTNBGURS', N'"Another of George Chiswell''s Asters. Geurney Slade has beautifully shaped double flowers in a rich deep blue. Flowers are produced in September to October at about 1m high. Part of a series of Asters based on the Ballard strains named after the mining villages of Somerset.INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (263, NULL, N'Aster', NULL, N'novi-belgii', N'''Harrison''s Blue''', N'( Symphyotrichum novi-belgii ''Harrison''s Blue'' )', N'ASTNBHB', N'A fully double deep violet Aster with good weatherproof blooms 4cm across. An excellent deep colour to contrast with the more typical mauvey blues. It stands tall at 100cm high and is a late season flowerer extending the season well into October. More purple than blue !', NULL, 1, N'0000000000000', N'A\Aster-novi-belgii-Harrison''s-Blue.jpg', NULL, NULL, NULL, 1, N'Default', 1, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (264, NULL, N'Aster', NULL, N'novi-belgii', N'''Little Boy Blue''', N'( Symphyotrichum novi-belgii ''Little Boy Blue'' )', N'ASTLBB', N'A richly coloured violet blue semi-double aster  with the advantage of a dwarf habit. With its bright  yellow centre; it''s makes a lovely complimentary accent amongst wilder pink michaelmas daisies. 60cm', N'1L', 1, N'0000000000000', N'A\Aster-novi-belgii-Little-Boy-Blue-(3).jpg', NULL, NULL, NULL, 0, N'Default', 0, NULL, 0)
INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (265, NULL, N'Aster', NULL, N'novi-belgii', N'''Little Man in Blue''', N'( Symphyotrichum novi-belgii ''Little Man in Blue'' )', N'ASTNBLMIB', N'Intermediate between the very dwarf and tall border Asters. A stocky plant is smothered in smallish lialc-blue daisies and is ideal for the edge or near front of the border to give a dash of late colour. An excellent late nectar source. Late September 50cm. Flowers 3cm.', NULL, 1, N'0000000000000', N'A\Aster-novi-belgii-Little-Man-in-Blue-(3).jpg', NULL, NULL, NULL, 0, N'Default', 0, NULL, 0)
