INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (999, NULL, N'Geum', NULL, NULL, N'''Lemon Delight''', NULL, N'GEUMLEMD', N'"When it first opens this reminds me of the simple cotton caps worn by maids in years gone by with frilly edges all gathered together with a band. At first nodding and a fresh lemon in colour the flowers open flatter and fade to a rich buttermilk. A good clumping variety.INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (1000, NULL, N'Geum', NULL, NULL, N'''Lemon Drops''', NULL, N'GEUMLEMDROP', N'"This subtle little geum is quite close to its rivale roots. The flowers all nod down with lemon yellow petals that remain in a tight little bell just showing proud of the calyx. A good little clump former.INSERT INTO [dbo].[TablePlants] ([Id], [GenusCross], [Genus], [SpeciesCross], [Species], [Variety], [Common], [SKU], [Desc], [PotSize], [ColourQueue], [Barcode], [Picture1], [Picture2], [Picture3], [Picture4], [AGM], [LabelColour], [Hide], [notes], [LabelStock]) VALUES (1001, NULL, N'Geum', NULL, NULL, N'''Mai Tai''', N'(Cocktails Series)', N'GEUMT', N'One of the denser clumping geums of mid size. The flowers are large frilly and a beautiful red stained peach. Keep cutting out old flowering stems to encourage a succession of flowers.', N'1L', 1, N'0000000000000', N'G\Geum-Mai-Tai.jpg', NULL, NULL, NULL, 0, N'Default', 0, NULL, 0)
